function getArticles(){const title1=document.querySelector("#article-1 h2"),title2=document.querySelector("#article-2 h2"),title3=document.querySelector("#article-3 h2"),text1=document.querySelector("#article-1 p"),text2=document.querySelector("#article-2 p"),text3=document.querySelector("#article-3 p"),image1=document.querySelector("#article-1 img"),image2=document.querySelector("#article-2 img"),image3=document.querySelector("#article-3 img"),video1=document.querySelector("#article-1 video"),video2=document.querySelector("#article-2 video"),video3=document.querySelector("#article-3 video"),day1=document.querySelector("#article-1 .article-day"),day2=document.querySelector("#article-2 .article-day"),day3=document.querySelector("#article-3 .article-day"),month1=document.querySelector("#article-1 .article-month"),month2=document.querySelector("#article-2 .article-month"),month3=document.querySelector("#article-3 .article-month");fetch("./db.json").then(response=>response.json()).then(articlesArr=>{console.log(articlesArr),title1.innerHTML=articlesArr[0].title,title2.innerHTML=articlesArr[1].title,title3.innerHTML=articlesArr[2].title,text1.innerHTML=articlesArr[0].description,text2.innerHTML=articlesArr[1].description,text3.innerHTML=articlesArr[2].description,image1.src="./images/"+articlesArr[0].img,image2.src="./images/"+articlesArr[1].img,image3.src="./images/"+articlesArr[2].img,video1.poster="./images/"+articlesArr[0].img,video2.poster="./images/"+articlesArr[1].img,video3.poster="./images/"+articlesArr[2].img;let date1Arr=articlesArr[0].date.split(" ");day1.innerHTML=date1Arr[0],month1.innerHTML=date1Arr[1];let date2Arr=articlesArr[1].date.split(" ");day2.innerHTML=date2Arr[0],month2.innerHTML=date2Arr[1];let date3Arr=articlesArr[2].date.split(" ");return day3.innerHTML=date3Arr[0],month3.innerHTML=date3Arr[1],articlesArr})}getArticles();